mapper.select:
SELECT A.i_user, A.user_id, A.user_pw, A.salt, A.nm, A.nick, A.email, A.sm, A.profile_img, A.r_dt, A.m_dt, B.myPoint, B.totalPoint, B.myCash, B.totalCash FROM t_user A
LEFT JOIN t_myPoint B ON A.i_user = B.i_user
WHERE i_user = #{i_user} user_id = #{user_id}

SELECT A.i_user, A.user_id, A.nm, A.nick, A.email, A.sm, A.profile_img, A.r_dt, A.m_dt, B.myPoint, B.totalPoint, B.myCash, B.totalCash FROM t_user A
LEFT JOIN t_myPoint B ON A.i_user = B.i_user
WHERE A.i_user = #{i_user}

SELECT A.i_user, A.user_id, A.user_pw, A.salt, A.nm, A.nick, A.email, B.myPoint, B.myCash, A.sm, A.profile_img, A.r_dt, A.m_dt, B.totalPoint, B.totalCash FROM t_user A
LEFT JOIN t_myPoint B ON A.i_user = B.i_user
WHERE A.i_user = #{i_user}

mapper.update:
UPDATE t_myPoint
SET totalPoint = totalPoint + ${totalPoint},
myPoint = myPoint + ${myPoint}
WHERE i_user = #{i_user}

UPDATE t_myPoint
SET myPoint = #{myPoint},
myCash = myCash + ${myCash}
WHERE i_user = #{i_user}

UPDATE t_user
SET user_pw = #{user_pw}
,salt = #{salt}
WHERE i_user = #{i_user}

UPDATE t_user
SET profile_img = #{profile_img}
WHERE i_user = #{i_user}

UPDATE t_user
SET profile_img = NULL
WHERE i_user = #{i_user}

UPDATE t_user
SET nick = #{nick},
email = #{email},
sm = #{sm}
WHERE i_user = #{i_user}
